package Simple;

//Swap 2 numbers without 3rd variable

public class Swap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a,b;
		a=10;
		b=20;
		System.out.println("a = "+a);
		System.out.println("b = "+b);
		a = a+b;
		b = a-b;
		a = a-b;
		System.out.println("a = "+a);
		System.out.println("b = "+b);
		

	}

}
